// walker.js
// testing file linking

class Walker {
  constructor(x, y, pointColor, speed, size) {
    this.x = x;
    this.y = y;
    this.pointColor = pointColor;
    this.speed = speed;
    this.size = size;
  }

  render() {
    stroke(this.pointColor);
    strokeWeight(this.size);
    point(this.x, this.y);
  }
  move() {
    let chance = Math.floor(random(4));
    if (chance == 0) {
      this.x += this.speed;
    } else if (chance == 1) {
      this.x -= this.speed;
    } else if (chance == 2) {
      this.y -= this.speed;
    } else if (chance == 3) {
      this.y += this.speed;
    }
  }
}

