class Walker {
  constructor(x, y, pointColor, speed, size) {
    this.x = x;
    this.y = y;
    this.pointColor = pointColor;
    this.speed = speed;
    this.size = size;
    this.tx = 0;
    this.ty = 1000000;
  }

  render() {
    stroke(this.pointColor);
    strokeWeight(this.size);
    point(this.x, this.y);
  }
  move() {

    this.x = map(noise(this.tx), 0, 1, 0, width);
    this.y = map(noise(this.ty), 0, 1, 0, height);
    this.tx += 0.01;
    this.ty += 0.01;
  }
}

