class BouncingBall {
  constructor(
    x,
    y,
    envWidth,
    envHeight,
    numBalls = 1,
    sizeMultiplier = 1,
    speedMultiplier = 1,
    gravityMultiplier = 1,
    damping
  ) {
    // top-left of bounding box
    this.x = x;
    this.y = y;

    this.envWidth = envWidth;
    this.envHeight = envHeight;

    this.radius = envHeight * 0.02 * sizeMultiplier;
    this.gravity = envHeight * 0.002 * gravityMultiplier;
    this.damping = damping;

    this.balls = [];

    for (let i = 0; i < numBalls; i++) {
      this.balls.push({
        position: createVector(
          x + random(this.radius, envWidth - this.radius),
          y + random(this.radius, envHeight - this.radius)
        ),
        velocity: createVector(
          random(-1, 1) * envWidth * 0.01 * speedMultiplier,
          random(-1, 1) * envHeight * 0.01 * speedMultiplier
        )
      });
    }
  }

  update() {
    const left   = this.x + this.radius;
    const right  = this.x + this.envWidth - this.radius;
    const top    = this.y + this.radius;
    const bottom = this.y + this.envHeight - this.radius;

    for (let ball of this.balls) {
      // gravity
      ball.velocity.y += this.gravity;

      // motion
      ball.position.add(ball.velocity);

      // floor
      if (ball.position.y > bottom) {
        ball.position.y = bottom;
        ball.velocity.y *= -this.damping;
      }

      // ceiling (optional, but correct)
      if (ball.position.y < top) {
        ball.position.y = top;
        ball.velocity.y *= -this.damping;
      }

      // walls
      if (ball.position.x < left || ball.position.x > right) {
        ball.velocity.x *= -this.damping;
        ball.position.x = constrain(ball.position.x, left, right);
      }
    }
  }

  draw() {
    noFill();
    stroke(0);

    // optional: visualize bounding box
    rect(this.x, this.y, this.envWidth, this.envHeight);

    for (let ball of this.balls) {
      circle(ball.position.x, ball.position.y, this.radius * 2);
    }
  }
}

