class BouncingBall {
  constructor(
    x,
    y,
    envWidth,
    envHeight,
    numBalls = 1,
    sizeMultiplier = 1,
    speedMultiplier = 1,
    gravityMultiplier = 1,
    damping,
    colorList = []          // NEW ARG
  ) {
    // top-left of bounding box
    this.x = x;
    this.y = y;

    this.envWidth = envWidth;
    this.envHeight = envHeight;

    this.radius = envHeight * 0.02 * sizeMultiplier;
    this.gravity = envHeight * 0.002 * gravityMultiplier;
    this.damping = damping;

    this.balls = [];

    for (let i = 0; i < numBalls; i++) {
      this.balls.push({
        position: createVector(
          x + random(this.radius, envWidth - this.radius),
          y + random(this.radius, envHeight - this.radius)
        ),
        velocity: createVector(
          1 * envWidth * 0.01 * speedMultiplier,
          1 * envHeight * 0.01 * speedMultiplier
        ),
        color: colorList.length > 0
          ? colorList[i % colorList.length]   //  cycle colors
          : color(0)                           // fallback
      });
    }
  }

  update() {
