let ball;

let tx = 0;
let ty = 10000;
let colors = ["#3f88c5", "#f49d37", "#140f2d", "#f22b29"];
function setup() {
  createCanvas(400, 400);
  ball = new BouncingBall(width/2, height/2, 100, 100, 150, 0.2, 0.2, colors);

  ("#69140E");
}

function draw() {
  background("rgba(105, 20, 14, 0.05)");

  // 1) mouse target (container stays on canvas)
  const targetX = constrain(
    mouseX - ball.envWidth / 2,
    0,
    width - ball.envWidth,
  );
  const targetY = constrain(
    mouseY - ball.envHeight / 2,
    0,
    height - ball.envHeight,
  );

  // 2) Perlin wander direction (angle from noise)
  const angle = noise(tx, ty) * TWO_PI * 2.0; // 0..~4
  const wanderStep = 3; // pixels per frame (wander strength)
  let wx = cos(angle) * wanderStep;
  let wy = sin(angle) * wanderStep;

  // 3) Seek direction toward mouse (normalized vector)
  const cx = ball.x + ball.envWidth / 2; // container center
  const cy = ball.y + ball.envHeight / 2;

  let sx = targetX + ball.envWidth / 2 - cx;
  let sy = targetY + ball.envHeight / 2 - cy;

  const d = sqrt(sx * sx + sy * sy) + 1e-6;
  sx /= d;
  sy /= d;

  const seekStep = 3.0; // pixels per frame (how strongly it "tends" to mouse)
  sx *= seekStep;
  sy *= seekStep;

  // 4) Blend: mostly wander, but biased toward mouse
  const wanderWeight = 0.35; // more = wilder
  const seekWeight = 0.25; // more = more goal-seeking

  const vx = wx * wanderWeight + sx * seekWeight;
  const vy = wy * wanderWeight + sy * seekWeight;

  // 5) Apply movement (and clamp)
  const newX = constrain(ball.x + vx, 0, width - ball.envWidth);
  const newY = constrain(ball.y + vy, 0, height - ball.envHeight);

  const dx = newX - ball.x;
  const dy = newY - ball.y;

  ball.x = newX;
  ball.y = newY;

  for (let b of ball.balls) {
    b.position.x += dx;
    b.position.y += dy;
  }

  // 6) Advance Perlin time
  tx += 0.01;
  ty += 0.01;

  ball.update();
  ball.draw();
}

