// follower.js
class Follower {
  constructor(posX, posY, size, color, count, rotationSpeed) {
    this.position = createVector(posX, posY);
    this.velocity = createVector(0, 0);

    this.size = size;
    this.color = color;

    this.maxSpeed = 24;
    this.maxForce = 0.2;
    this.damping = 0.9;

    this.angle = 0;
    this.rotationSpeed = rotationSpeed; // base rotation speed
    this.count = count
  }

  update(target) {
    // desired = target - position
    let desired = p5.Vector.sub(target, this.position);

    // slow down when close
    let d = desired.mag();
    if (d < 120) {
      let m = map(d, 0, 120, 0, this.maxSpeed);
      desired.setMag(m);
    } else {
      desired.setMag(this.maxSpeed);
    }

    // steering force
    let steer = p5.Vector.sub(desired, this.velocity);
    steer.limit(this.maxForce);

    // apply motion
    this.velocity.add(steer);
    this.velocity.mult(this.damping);
    this.position.add(this.velocity);
  }

  render() {
    push();
    translate(this.position.x, this.position.y);

    // rotate based on motion (faster when moving)
    let spin = this.rotationSpeed + this.velocity.mag() * 0.01;
    this.angle += spin;
    rotate(this.angle);

    noStroke();
    fill(this.color);

    let ringRadius = this.size * 0.8;
    let dotSize = this.size * 0.25;

    for (let i = 0; i < this.count; i++) {
      let a = map(i, 0, this.count, 0, TWO_PI);
      let x = cos(a) * ringRadius;
      let y = sin(a) * ringRadius;
      circle(x, y, dotSize);
    }

    pop();
  }
}

