// follower.js
class Follower {
  constructor(posX, posY, size, colors, count, rotationSpeed) {
    this.position = createVector(posX, posY);
    this.velocity = createVector(0, 0);

    this.size = size;
    this.colors = colors;          // array of colors
    this.count = count;            // number of dots
    this.rotationSpeed = rotationSpeed;

    // motion tuning
    this.maxSpeed = 24;
    this.maxForce = 0.2;
    this.damping = 0.9;

    // render state
    this.angle = 0;

    // spiral/ring tuning (feel free to tweak)
    this.turns = 2;              // how many spiral rotations from center->edge
    this.peakAt = 0.65;            // where dots stop growing and start shrinking
    this.fadeStart = 0.8;          // start fading near the edge
  }

  update(target) {
    // desired velocity toward target
    let desired = p5.Vector.sub(target, this.position);

    // slow down when close (arrival)
    let d = desired.mag();
    if (d < 120) {
      let m = map(d, 0, 120, 0, this.maxSpeed, true);
      desired.setMag(m);
    } else {
      desired.setMag(this.maxSpeed);
    }

    // steering force = desired - current velocity
    let steer = p5.Vector.sub(desired, this.velocity);
    steer.limit(this.maxForce);

    // apply motion
    this.velocity.add(steer);
    this.velocity.mult(this.damping);
    this.position.add(this.velocity);
  }

  render() {
    push();
    translate(this.position.x, this.position.y);

    // rotate a bit faster when moving
    let spin = this.rotationSpeed + this.velocity.mag() * 0.01;
    this.angle += spin;
    rotate(this.angle);

    noStroke();

    // outer boundary ("end of the ring")
    let ringRadius = this.size * 1.2;

    // dot size baseline (overall thickness feel)
    let baseDot = this.size * 0.1;

    // safety: if colors missing, fallback
    let hasColors = this.colors && this.colors.length > 0;

    for (let i = 0; i < this.count; i++) {
      // progress outward 0..1
      let t = (this.count <= 1) ? 1 : i / (this.count - 1);

      // spiral position
      let a = t * TWO_PI * this.turns;   // multiple turns
      let rad = t * ringRadius;          // radius grows outward
      let x = cos(a) * rad;
      let y = sin(a) * rad;

      // size profile: grow -> shrink -> vanish at edge
      let peakAt = this.peakAt;
      let grow = map(t, 0, peakAt, 0.1, 1.0, true);
      let shrink = map(t, peakAt, 1.0, 1.0, 0.0, true);
      let sizeFactor = (t < peakAt) ? grow : shrink;

      let dotSize = baseDot + baseDot * 2.5 * sizeFactor;

      // fade out near the ring edge
      let alpha = map(t, this.fadeStart, 1.0, 255, 0, true);

      // color cycle
      if (hasColors) {
        let c = this.colors[i % this.colors.length];
        fill(red(c), green(c), blue(c), alpha);
      } else {
        fill(0, alpha);
      }

      circle(x, y, dotSize);
    }

    pop();
  }
}

