// --- Perlin walker state ---
let walkerPos;
let tx = 0;
let ty = 10000;

function setup() {
  createCanvas(400, 400);

  f = new Follower(width / 2, height / 2, 48, colors, 10, 0.02);

  // start walker in center
  walkerPos = createVector(width / 2, height / 2);

  fill("#0000000");
  rect(0, 0, width, height);
}

function draw() {
  background("rgba(0, 0, 0, 0.05)");

  // --- Perlin-walker target (vector) ---
  // raw noise positions
  let x = map(noise(tx), 0, 1, 0, width);
  let y = map(noise(ty), 0, 1, 0, height);

  // constrain (extra safety)
  x = constrain(x, 0, width);
  y = constrain(y, 0, height);

  walkerPos.set(x, y);

  // advance noise inputs (smaller = smoother)
  tx += 0.01;
  ty += 0.01;

  // follower follows the walker
  f.update(walkerPos);
  f.render();

  // (optional) show the walker as a small dot
  noStroke();
  fill("red");
  circle(walkerPos.x, walkerPos.y, 6);
}

