class VectorDrawer {
  constructor(opts = {}) {
    this.strokeCol = opts.strokeCol ?? 0;
    this.fillCol = opts.fillCol ?? 0;
    this.headScale = opts.headScale ?? 0.02;
    this.weightScale = opts.weightScale ?? 0.015;
    this.minHead = opts.minHead ?? 2;
    this.maxHead = opts.maxHead ?? 40;
    this.minWeight = opts.minWeight ?? 0.5;
    this.maxWeight = opts.maxWeight ?? 8;
  }

  drawFromPoints(x1, y1, x2, y2) {
    const dx = x2 - x1;
    const dy = y2 - y1;

    const angle = atan2(dy, dx);
    const magnitude = sqrt(dx * dx + dy * dy);

    const scaledMag = (magnitude * height) / width;

    const w = constrain(
      scaledMag * this.weightScale,
      this.minWeight,
      this.maxWeight
    );

    const head = constrain(
      scaledMag * this.headScale,
      this.minHead,
      this.maxHead
    );

    push();
    stroke(this.strokeCol);
    fill(this.fillCol);
    strokeWeight(w);

    line(x1, y1, x2, y2);

    translate(x2, y2);
    rotate(angle);
    triangle(0, 0, -head, head / 2, -head, -head / 2);
    pop();
  }

  drawFromAngleAndMag(angle, magnitude, startX = 0, startY = 0) {
    const x2 = startX + cos(angle) * magnitude;
    const y2 = startY + sin(angle) * magnitude;
    this.drawFromPoints(startX, startY, x2, y2);
  }

  drawFromVector(v, startX = 0, startY = 0) {
    this.drawFromPoints(startX, startY, startX + v.x, startY + v.y);
  }
}
