// target.js

class Target {
  constructor(
    x,
    y,
    pointColor,
    active,
    expandRate,
    popSize,
    activateRange,
    lineThickness,
  ) {
    this.x = x;
    this.y = y;
    this.pointColor = pointColor;

    this.size = 1;
    this.active = false;

    this.expandRate = expandRate;
    this.popSize = popSize;
    this.activateRange = activateRange;
    this.lineThickness = lineThickness;

    // controls how "circle-like" it gets near the end
    this.maxSides = 10; // try 30120
  }

  render() {
    strokeWeight(this.lineThickness);
    noFill();
    stroke(this.pointColor);

    if (!this.active) return;

    // grow
    this.size += this.expandRate;

    // progress 0..1
    const p = constrain(this.size / this.popSize, 0, 1);

    // sides from 3 up to maxSides
    const sides = floor(lerp(3, this.maxSides, p));

    // draw polygon (using size as diameter, like your circle() did)
    this.drawRegularPolygon(this.x, this.y, this.size * 0.5, sides);

    // pop + reset
    if (this.size >= this.popSize) {
      this.active = false;
      this.size = 1;
    }
  }

  hit(walker) {
    if (!this.active) {
      if (dist(walker.x, walker.y, this.x, this.y) < this.activateRange) {
        this.active = true;
      }
    }
  }

  drawRegularPolygon(cx, cy, r, sides) {
    // safety clamp
    sides = max(3, sides);

    beginShape();
    for (let i = 0; i < sides; i++) {
      const a = (TWO_PI * i) / sides;
      const px = cx + cos(a) * r;
      const py = cy + sin(a) * r;
      vertex(px, py);
    }
    endShape(CLOSE);
  }
}

