// target.js

class Target {
  constructor(
    x,
    y,
    pointColor0, // was pointColor
    pointColor1, // NEW: alternate color
    active,
    expandRate,
    popSize,
    activateRange,
    lineThickness,
  ) {
    this.x = x;
    this.y = y;

    this.pointColor0 = pointColor0;
    this.pointColor1 = pointColor1;

    this.size = 1;
    this.active = false;

    this.expandRate = expandRate;
    this.popSize = popSize;
    this.activateRange = activateRange;
    this.lineThickness = lineThickness;

    // animation state
    this.growing = true; // true = grow, false = shrink
    this.colorFlip = false; // toggles each activation
  }

  render() {
    if (!this.active) return;

    // appearance
    strokeWeight(this.lineThickness);
    noStroke();

    // fill color depends on flip state
    fill(this.colorFlip ? this.pointColor1 : this.pointColor0);

    // draw filled circle
    circle(this.x, this.y, this.size);

    // animate size
    if (this.growing) {
      this.size += this.expandRate;

      if (this.size >= this.popSize) {
        this.size = this.popSize;
        this.growing = false; // start shrinking
      }
    } else {
      this.size -= this.expandRate;

      if (this.size <= 1) {
        this.size = 1;
        this.active = false; // disappear
        // keep growing=false? doesnt matter because next hit resets it
      }
    }
  }

  hit(walker) {
    if (this.active) return;

    if (dist(walker.x, walker.y, this.x, this.y) < this.activateRange) {
      this.active = true;

      // reset animation
