let balls = [];
let rows, cols;
let colors = ["#69747c", "#6baa75", "#84dd63", "#cbff4d"];

let t = 0;

function setup() {
  createCanvas(400, 400);

  rows = 5;
  cols = 30;

  const cellW = width / cols;
  const cellH = height / rows;

  for (let r = 0; r < rows; r++) {
    for (let c = 0; c < cols; c++) {
      // jitter each cell a bit (static offset)
      const ox = random(-cellW * 0.25, cellW * 0.25);
      const oy = random(-cellH * 0.25, cellH * 0.25);

      const x = c * cellW + ox;
      const y = r * cellH + oy;

      const ball = new BouncingBall(
        x,
        y,
        cellW,
        cellH,
        200,
        0.2,
        2,
        0,
        1,
        colors
      );

      // phase offsets per instance (based on grid index)
      ball.tx = r * 0.25 + c * 0.25;
      ball.ty = r * 0.25 - c * 0.25;

      // store anchor position for trig drifting
      ball.baseX = x;
      ball.baseY = y;

      balls.push(ball);
    }
  }
}

function draw() {
  background("rgba(84, 84, 84, 0.2)");
  t += 0.03;

  for (let ball of balls) {
    // trig drift amount (relative to each cell size)
    const ampX = ball.envWidth * 0.45;
    const ampY = ball.envHeight * 0.45;

    // wave offsets (phase differs per instance)
    const offsetX = sin(t + ball.tx) * ampX;
    const offsetY = cos(t + ball.ty) * ampY;

    // new container position
    const newX = ball.baseX + offsetX;
    const newY = ball.baseY + offsetY;

    // move everything by the same delta so the system stays intact
    const dx = newX - ball.x;
    const dy = newY - ball.y;

    ball.x = newX;
    ball.y = newY;

    for (let b of ball.balls) {
      b.position.x += dx;
      b.position.y += dy;
    }

    ball.update();
    ball.draw();
  }
}

